df %>% select(COUNTRY) %>% group_by(COUNTRY) %>% summarise(CountryAmount = n()) %>% top_n(10) %>% ggplot(aes(x = COUNTRY, y = CountryAmount)) + geom_bar(stat = "identity")
df %>% select(USERS, COUNTRY) %>% group_by(COUNTRY, USERS) %>% summarise(n = n()) %>% filter(COUNTRY %in% c("USA", "China (PR)", "Russia", "Canada", "ESA", "Germany", "India", "Japan", "Multinational", "United Kingdom"), USERS %in% c("Civil", "Commercial", "Government", "Military")) %>% ggplot(aes(x = USERS, y = n, color = USERS)) + geom_bar(stat = "identity") + facet_wrap(~COUNTRY)
df %>% select(PERIGEE, APOGEE) %>% ggplot(aes(x = as.factor(APOGEE), y = as.factor(PERIGEE))) + geom_point()